(function(){[].forEach.call(document.querySelectorAll("[tagcloud]"),function(a){a.innerHTML="<span>"+a.innerHTML.split(/\n/).join("</span> <span>")+"</span>";[].forEach.call(a.querySelectorAll("span"),function(e){var d=Math.random()*150+50;if(a.hasAttribute("large")){d=d*1.2}e.style.fontSize=d+"%";if(a.hasAttribute("bw")){var c=Math.round(Math.random()*155);e.style.color="rgb("+c+","+c+","+c+")"}else{if(a.hasAttribute("blue")){var b=Math.round(Math.random()*255);var c=Math.round(Math.random()*155);e.style.color="rgb("+c+","+c+","+b+")"}else{e.style.color="hsl("+Math.random()*360+", 40%, 50%)"}}e.classList.add("clouditem")})})})();